import init, * as wasm from './pkg/libemulator.js'

const WIDTH = 64;
const HEIGHT = 32;
const SCALE = 10;
const TICKS_PER_FRAME = 10;
let anim_frame = 0;
const SCALED_HEIGHT = HEIGHT * SCALE;
const SCALED_WIDTH = WIDTH * SCALE;
const canvas = document.getElementById('display_stage');
canvas.height = SCALED_HEIGHT;
canvas.width = SCALED_WIDTH;

const ctx = canvas.getContext('2d');
const snd = new Audio("data:audio/wav;base64,");

ctx.fillStyle = '#000000';
ctx.fillRect(0, 0, SCALED_WIDTH, SCALED_HEIGHT);

const run = async () => {
  await init();
  let emulator = new wasm.WasmEmu();
  let input = document.getElementById("rom_select");
  document.addEventListener('keydown', e => emulator.keypress(e, true));
  document.addEventListener('keyup', e => emulator.keypress(e, false));
  input.addEventListener("change", (e) => {
    if (anim_frame != 0) {
      window.cancelAnimationFrame(anim_frame);
    }
    let file = e.target.files[0];
    if (!file) return;
    let fileReader = new FileReader();
    fileReader.onload = (e) => {
      let buffer = fileReader.result;
      const rom = new Uint8Array(buffer);
      emulator.reset();
      emulator.load_rom(rom);
      mainLoop(emulator);
    }
    fileReader.readAsArrayBuffer(file);
  }, false)
};

run();

function mainLoop(emulator) {
  for (let i = 0; i < TICKS_PER_FRAME; i++) {
    emulator.tick()
  }
  if (emulator.tick_timers()) {
    beep();
  }
  ctx.fillStyle = 'black';
  ctx.fillRect(0, 0, WIDTH * SCALE, HEIGHT * SCALE);
  ctx.fillStyle = 'white';
  emulator.draw_screen(SCALE);
  anim_frame = window.requestAnimationFrame(() => mainLoop(emulator));
}

function beep() {
  snd.play();
}